mapper.select:
	select * from  	
  	(
  		select rownum as rnum, b.* from 
  			(select /*+ index_desc(board board_pk_bno) */ bno, title, writer, readCnt, writeTime, commentCnt from board  

				writer=#{writer} 

  			 order by bno desc) b
  		where rownum <=#{end}
  	)
  	where rnum>=#{start}

mapper.update:
update board
title=#{title}, content=#{content}, readCnt=readCnt+1, goodCnt=goodCnt+1, badCnt=badCnt+1, commentCnt=#{commentCnt},
where bno=#{bno}
